package condition;

public class ifelseifladder2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number = -13;
		if (number > 0) {  //fail
			System.out.println("POSITIVE");
		} else if (number < 0) { //true
			System.out.println("NEGATIVE");
			
		} else {
			System.out.println("ZERO");
		}
	}

}
